// lib/client/html.ts
var textToHTML = (htmlString) => {
  const div = document.createElement("div");
  div.innerHTML = htmlString;
  const firstChild = div.firstElementChild;
  if (!firstChild) {
    console.error("No valid HTML element found in", htmlString);
    return document.createElement("div");
  }
  return firstChild;
};
// lib/client/render.ts
var $loop = (items, fn) => items.map(fn).join("");
// lib/client/tags.ts
var html = (strings, ...values) => String.raw({ raw: strings }, ...values);
var css = (strings, ...values) => String.raw({ raw: strings }, ...values);
export {
  textToHTML,
  html,
  css,
  $loop
};
